import osimport openaifrom bot_apis.maedchen_ai.chat_db import chatOperatorclass ChatGPTAgent:    def __init__(self):        # Setting the API key to use the OpenAI API        openai.api_key = os.getenv("OPENAI_API_KEY")        self.messages = []        self.db = chatOperator()    def chat(self, message):        self.messages.append({"role": "user", "content": message})        response = openai.ChatCompletion.create(            model="gpt-3.5-turbo", messages=self.messages        )        self.messages.append(            {"role": "assistant", "content": response["choices"][0]["message"].content}        )        return response["choices"][0]["message"]        def start(self):        self.messages = []        message = "Hello, you are my personal assistance, you should answer my questions and chat with me!"        return self.chat(message)        def teabreak(self, chat_id):        # store self.messages to mongodb        if self.db.is_chat_exist(chat_id):            self.db.update_exist(chat_id, self.messages)        else:            self.db.create_one(chat_id, self.messages)        return        def reload(self, chat_id, message):        ## fetch previous records from mongodb        history = self.db.get_conv(chat_id)        if len(history) > 0:            self.messages = history["message"]        else:            self.messages = []        return self.chat(message)    def restart(self, chat_id):        self.db.delete_many([chat_id])        return self.start()if __name__ == "__main__":    c = ChatGPTAgent()    c.chat("hello there")    print(c.chat("can you show me how to write a recursive function in python?"))